/*
	Use inside iframe container
	with
	var iframeResizer = new IframeResizer('http://example.com'); // authorized origin
 */
var IframeResizer=function(e){window.addEventListener("message",function(n){if(n.origin!==e)return!1;for(var t=document.getElementsByTagName("iframe"),i=0;i<t.length;i++)if(t[i].contentWindow===n.source){var r=n.data.split(":")[1];t[i].style.height=r+"px"}},!1)};
